let deleteTaskListner=function(e){console.log(e.currentTarget.id),deleteTask(e.currentTarget.id)};function deleteTask(e){e=e.split("-")[1],$.ajax({type:"GET",url:"/task/delete",data:{task_id:e},success:function(e){new Noty({theme:"metroui",text:'<i class="fa-solid fa-trash"></i>Task Deleted',type:"success",layout:"topRight",timeout:1500}).show(),$(`#${e.card_id}`).remove()}})}const deletebuttons=document.querySelectorAll(".delete-button");for(let e=0;e<deletebuttons.length;e++)deletebuttons[e].addEventListener("click",deleteTaskListner);