let toggleStatusListner=function(e){toggleStatus(e.currentTarget.id)};function toggleStatus(e){$.ajax({type:"POST",url:"/task/toggle",data:{task_id:e},success:function(e){$(`#${e.card_id}`).hasClass("checked")?$(`#${e.card_id}`).removeClass("checked"):$(`#${e.card_id}`).addClass("checked")}})}const checkedInputs=document.querySelectorAll('input[type="checkbox"]');for(let e=0;e<checkedInputs.length;e++)checkedInputs[e].addEventListener("change",toggleStatusListner);